mapper.update:
UPDATE plan
SET title = #{title},
    content = #{content},
    plan_time = #{planTime},
    status = #{status}
WHERE id = #{id}

mapper.select:
SELECT

FROM plan p
LEFT JOIN user u ON p.user_id = u.id
WHERE p.user_id = #{userId}

    AND p.plan_time >= #{startTime}


    AND p.plan_time <= #{endTime}

ORDER BY p.plan_time ASC, p.create_time DESC
